package suga04;

public class IfTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int score = 59;
		
		//점수가 60점 이상이면 합격
		if(score >= 60) {
			//참일때 실행
			System.out.println("합격!");
		}		else {
			//거짓일때 실행
		System.out.println("불합격");	
		}
		
				
		
		
		
	}

}
